import { productConstants } from "../actions/constants";

const initState = {
  categories: [],
  loading: false,
  error: null,
};

export default (state = initState, action) => {
    switch (action.type) {
      case productConstants.ADD_NEW_PRODUCT_REQUEST:
        state = {
          ...state,
          loading:true
        };
        break;
  
      case productConstants.ADD_NEW_PRODUCT_SUCCESS:
  
        const cate_ =action.payload.categ;
        const updatedCategories = buildNewCategories(cate_.parentId,state.categories,cate_);
  
        state = {
          ...state,
          categories: updatedCategories ,
          loading:false
        };
        break;
  
      case productConstants.ADD_NEW_PRODUCT_FAILURE:
        state = {
          ...initState,
        };
        break;
    }
  
    return state;
  };
