import { categoryConstants } from "../actions/constants";

const initState = {
  categories: [],
  loading: false,
  error: null,
};

//This function refresh the page with newly added category.
// This function call in 58 th line in this file
const buildNewCategories =(parentId,categories, category) => {

  let myCategories = [];

  if(parentId === undefined){
    return [
      ...categories,
      {
        _id: category._id,
        name: category.name,
        slug: category.slug,
        children: []

      }
    ];
  }

  for(let cat of categories){

    if(cat._id == parentId){
      myCategories.push({
        ...cat,
        children: cat.children && cat.children.length  > 0 ? buildNewCategories(parentId,[...cat.children, {
          _id: category._id,
          name: category.name,
          slug: category.slug,
          parentId: category.parentId,
          children: category.children
        }],category) : []
      });

    }else{
      myCategories.push({
        ...cat,
        children: cat.children && cat.children.length  > 0 ? buildNewCategories(parentId,cat.children,category) : []
      });
    }
  }
  return myCategories;
}

export default (state = initState, action) => {
  switch (action.type) {
    case categoryConstants.GET_ALL_CATEGORY_SUCCESS:
      state = {
        ...state,
        categories: action.payload.categories,
      };
      break;

    case categoryConstants.ADD_NEW_CATEGORY_REQUEST:
      state = {
        ...state,
        loading:true
      };
      break;

    case categoryConstants.ADD_NEW_CATEGORY_SUCCESS:

      const cate_ =action.payload.categ;
      const updatedCategories = buildNewCategories(cate_.parentId,state.categories,cate_);

      state = {
        ...state,
        categories: updatedCategories ,
        loading:false
      };
      break;

    case categoryConstants.ADD_NEW_CATEGORY_FAILURE:
      state = {
        ...initState,
      };
      break;
  }

  return state;
};
