import { authConstants } from "../actions/constants";
const initState = {
    token:null,
    user: {
        firstName: '',
        lastName: '',
        email:'',
        picture: ''
    },
    authenticate: false,
    authenticating: false
};

export default (state = initState,action)=>{
    switch (action.type) {
        case authConstants.LOGIN_REQUEST:
            state = {
                ...state,
                authenticating: true
            }
            break;
    }

    return state;
}
