import React, { useEffect, useState } from "react";
import { Col, Container, Row, Button } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { addCategory, getAllCategory } from "../../actions";
import Layout from "../../components/Layout";
import Input from "../../components/UI/Input";
import Modal from '../../components/UI/Modal';
import CheckboxTree from "react-checkbox-tree";
import 'react-checkbox-tree/lib/react-checkbox-tree.css';
import { IoIosCheckboxOutline, IoIosCheckbox, IoIosArrowForward, IoIosArrowDown } from "react-icons/io";



export default function Category() {
  const category = useSelector((state) => state.category);
  const dispatch = useDispatch();
  const [show, setShow] = useState(false);
  const [categoryName,setCategoryName] =useState('');
  const [parentCategoryId,setParentCategoryId] =useState('');
  const [categoryImage,setCategoryImage] =useState('');
  const [checked, setChecked] = useState([]);
  const [expanded, setExpanded] = useState([]);
  const [checkedArray, setCheckedArray] = useState([]);
  const [expandedArray, setExpandedArray] = useState([]);
  const [updateCategoryModal, setUpdateCategoryModal] = useState(false);
  // const [updateCategory, setUpdateCategory] = useState(false);


  ////////////////////Now this code part in the App.js 24 line//////////
  // useEffect(() => {
  //   dispatch(getAllCategory()); 
  // }, []);

  const handleClose = () => {
    const form = new FormData();

    form.append('name',categoryName);
    form.append('parentId',parentCategoryId);
    form.append('categoryImage',categoryImage);
    dispatch(addCategory(form)); 
    setCategoryName('');
    setParentCategoryId('');
  
    setShow(false);
    
  } 
  
  const handleShow = () => setShow(true);

  const renderCategories = (categories) => {
    let myCategories = [];
    for (let category__ of categories) {
      myCategories.push(
        {
          label: category__.name,
          value:category__._id,
          children:category__.children.length > 0 && renderCategories(category__.children)
        }
      );
    }
    return myCategories;
  };

  const createCategoryList = (categories, options =[]) => {
      for(let category of categories){
          options.push({ value: category._id, name: category.name });
          if(category.children.length > 0){
              createCategoryList(category.children, options)
          }
      }
      return options;
  }

  const handleCategoryImage = (e) =>{
      setCategoryImage(e.target.files[0]);
  }

  const updateCategory = () => {
    setUpdateCategoryModal(true);
  }

  return (
    <Layout sidebar>
      <Container>
        <Row>
          <Col md={12}>
            <div style={{ display: "flex", justifyContent: "space-between" }}>
              <h3>Category</h3>
              <button onClick={handleShow} type="button" class="btn btn-primary">Add</button>
            </div>
          </Col>
        </Row>
        <Row>
          <Col md={12}>
            {/* <ul>
                {renderCategories(category.categories)}
            </ul> */}
             <CheckboxTree
                nodes={renderCategories(category.categories)}
                checked={checked}
                expanded={expanded}
                onCheck={checked => setChecked( checked )}
                onExpand={expanded => setExpanded(expanded)}
                icons ={{
                  check: <IoIosCheckbox/>,
                  uncheck: <IoIosCheckboxOutline/>,
                  halfCheck: <IoIosCheckbox/>,
                  expandClose: <IoIosArrowForward/>,
                  expandOpen: <IoIosArrowDown/>,
                }}
            />
          </Col>
        </Row>
        <Row>
          <Col>
          <button  type="button" class="btn btn-primary">Delete</button>
          <button onClick={updateCategory} type="button" class="btn btn-primary">Edit</button>
          </Col>
        </Row>
      </Container>

      <Modal
      show={show}
      handleClose ={handleClose}
      modalTitle = {'Add New Category'}
      >  
        <Input 
                value={categoryName}
                placeh  older = {`Category Name`}
                onChange={(e)=>setCategoryName(e.target.value)}
            />
            <br></br>
            <select 
            className="form-control" 
            value ={parentCategoryId}
            onChange={(e)=> setParentCategoryId(e.target.value)}>
                <option>select category</option>
                {
                    createCategoryList(category.categories).map(option =>
                        <option key={option.value} value={option.value}>{option.name}</option>)
                }
            </select>
            <br></br>
            <input
                type="file"
                name="categoryImage"
                onChange ={handleCategoryImage}
            />

      </Modal>
 
      {/* Edit Categories */}
      <Modal
      show={updateCategoryModal}
      handleClose ={()=>setUpdateCategoryModal(true)}
      modalTitle = {'Add New Category'}
      >  
        <Input 
                value={categoryName}
                placeh  older = {`Category Name`}
                onChange={(e)=>setCategoryName(e.target.value)}
            />
            <br></br>
            <select 
            className="form-control" 
            value ={parentCategoryId}
            onChange={(e)=> setParentCategoryId(e.target.value)}>
                <option>select category</option>
                {
                    createCategoryList(category.categories).map(option =>
                        <option key={option.value} value={option.value}>{option.name}</option>)
                }
            </select>
            <br></br>
            <input
                type="file"
                name="categoryImage"
                onChange ={handleCategoryImage}
            />

      </Modal>
      
    </Layout>
  );
}
