import { categoryConstants } from "../actions/constants";

const initState = {
  categories: [],
  loading: false,
  error: null,
};

//This function refresh the page with newly added category.
// This function call in 34 th line in this file
const buildNewCategories =(id,categories, category) => {

  let myCategories = [];

  for(let cat of categories){

    if(cat.parentId && cat.parentId == id){
      myCategories.push({
        ...cat,
        children: cat.children && cat.children.length  > 0 ? buildNewCategories(id,cat.children,category) : []
      });

    }else{
      myCategories.push({
        ...cat,
        children: cat.children && cat.children.length  > 0 ? buildNewCategories(cat.children,category) : []
      });
    }
  }
  return myCategories;
}

export default (state = initState, action) => {
  switch (action.type) {
    case categoryConstants.GET_ALL_CATEGORY_SUCCESS:
      state = {
        ...state,
        categories: action.payload.categories,
      };
      break;

    case categoryConstants.ADD_NEW_CATEGORY_REQUEST:
      state = {
        ...state,
        loading:true
      };
      break;

    case categoryConstants.ADD_NEW_CATEGORY_SUCCESS:
      state = {
        ...state,
        categories: buildNewCategories(state.categories.action.payload.categ),
        loading:false
      };
      break;

    case categoryConstants.ADD_NEW_CATEGORY_FAILURE:
      state = {
        ...initState,
      };
      break;
  }

  return state;
};
