import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getProductsBySlug } from '../../actions';
import Layout from '../../Components/Layout'
import { useLocation } from 'react-router-dom'
import './style.css';

export default function ProductListPage(props) {

    const dispatch = useDispatch();
    const location = useLocation();
    const product = useSelector(state => state.product);
    
    useEffect(() => {
        dispatch(getProductsBySlug( location.pathname ));
    }, []);
    
    return (
        <Layout>
            {
                Object.keys(product.productsByPrice).map((key, index)=>{
                    return(
                        <div className="card">
                <div className="cardHeader">
                    <div>Samsung mobile under 10k</div>
                    <button>View all</button>
                </div>
                <div>
                    <div className="productContainer">
                        <div className="productImgContainer">
                            <img src="http://localhost:2000/public/ct5AKoWal-M02-1.jpeg" alt=""/>
                        </div>   
                        <div className="productInfo">
                            <div className="productTitle">Samsung j7 Prime</div>
                            <div>
                                <span>4.3</span>&nbsp;
                                <span>3343</span>
                            </div>
                            <div className="productPrice">5000</div>
                        </div>    
                    </div>
                </div>
            </div>
                    );
                })
            }
        </Layout>
    )
}
