import { productsConstants } from "../actions/constants"

const initState = {
    products:[],
    productsByPrice: {  //This productByPrice is object so we can't use map function to display this details in product container
        under5k: [],
        under10k: [],
        under15k: [],
        under20k: [],
        upper20k: []
    }
}

const productReducer = () => ( state = initState, action) =>{
    switch(action.type){
        case productsConstants.GET_PRODUCTS_BY_SLUG:
            state={
                ...state,
                products: action.payload.products,
                productsByPrice: {
                    ...action.payload.productsByPrice
                }
            }
            break;
        default:
            break;
    }
    return state;    
}
export default productReducer;
